import React from 'react';

function LoginPage() {
  const handleLogin = () => {
    // Redirect to backend login route to initiate Azure AD OpenID Connect login
    window.location.href = 'http://localhost:5000/login';
  };

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '4px', textAlign: 'center' }}>
      <h2>Login</h2>
      <button onClick={handleLogin} style={{ padding: '10px 15px', fontSize: '16px' }}>
        Login with Azure AD
      </button>
    </div>
  );
}

export default LoginPage;
